INSERT INTO categories (category_id, concept_id, name, definition, extended_description, source, created_at, last_modified) VALUES
('5c9b94c2-1a6e-48f0-9001-123ac146b543', '3a7c27d1-35e4-47c3-a0d7-e189eb7c3fb6', 'Добродетель', 'Устойчивая черта характера, формируемая через практику и обеспечивающая правильное действие в соответствующих ситуациях', 'Добродетели являются средством достижения эвдемонии и представляют собой срединное состояние между избытком и недостатком. Они формируются через привычку и практику, требуя осознанности и рефлексии. Аристотель разделял добродетели на нравственные и интеллектуальные. Нравственные добродетели связаны с эмоциями и действиями, в то время как интеллектуальные относятся к способности размышлять и познавать.', 'Аристотель, "Никомахова этика", Книги II-IV', '2025-04-15 10:40:12.345+00', '2025-04-23 11:15:27.890+00'),

('7d8e5f32-9a1b-420c-b6c9-987dcfa63214', '3a7c27d1-35e4-47c3-a0d7-e189eb7c3fb6', 'Эвдемония', 'Состояние благополучия и процветания, конечная цель человеческой жизни, достигаемая через практику добродетелей', 'Эвдемония - это не мимолетное ощущение удовольствия, а состояние благополучия и самореализации на протяжении всей жизни. Она предполагает жизнь в соответствии с добродетелью и разумом, полное раскрытие человеческого потенциала. Аристотель считал эвдемонию самодостаточной и конечной целью всех человеческих стремлений.', 'Аристотель, "Никомахова этика", Книга I', '2025-04-15 10:45:33.678+00', '2025-04-22 15:18:42.127+00'),

('9e4a72b1-8c5d-4e6f-3a9b-543fdcba2108', '3a7c27d1-35e4-47c3-a0d7-e189eb7c3fb6', 'Золотая середина', 'Принцип, согласно которому добродетель находится между двумя крайностями - избытком и недостатком', 'Доктрина золотой середины (μεσότης) предполагает, что добродетель – это срединное состояние между двумя пороками: избытком и недостатком соответствующего качества. Например, мужество – это середина между безрассудством (избыток) и трусостью (недостаток). Важно отметить, что середина не является абсолютной или математической, а определяется относительно индивидуума и конкретных обстоятельств.', 'Аристотель, "Никомахова этика", Книга II', '2025-04-15 11:05:28.912+00', '2025-04-23 10:30:15.678+00'),

('2b3c6d9e-7f1a-4b5c-8d9e-1a2b3c4d5e6f', '3a7c27d1-35e4-47c3-a0d7-e189eb7c3fb6', 'Фронезис', 'Практическая мудрость или рассудительность, позволяющая правильно применять моральные принципы в конкретных ситуациях', 'Фронезис (φρόνησις) – это одна из ключевых интеллектуальных добродетелей, связанная со способностью принимать правильные решения в конкретных ситуациях на основе общих принципов. Она отличается от теоретической мудрости (софия) тем, что направлена на действие и практические вопросы. Фронезис необходим для правильного определения "золотой середины" в той или иной ситуации, учитывая все релевантные факторы.', 'Аристотель, "Никомахова этика", Книга VI', '2025-04-15 11:20:45.234+00', '2025-04-20 09:11:53.489+00');

